import axios from "axios";
import { useState, useEffect } from "react";
import Product from "components/Product/Product";
import { CardProductProps, RouteComponentProps } from "pages/CardProduct/types";
import Text from "components/Text/Text";
import Button from "components/Button/Button";
import { StyledFavorites } from "modules/NewProductsList/NewProductsListStyled";
import { Row, Col, Image, ListGroup, Card } from "react-bootstrap";

export const AllProducts = (): any => {
	const [products, setProducts] = useState<any[]>([]);

	useEffect((): any => {
		const fetchProducts = async () => {
			const { data } = await axios.get("/api/products");

			setProducts(data);
		};

		fetchProducts();
	}, []);

	console.log(products);

	return (
		<>
			<Row>
				{products.map((product): any => (
					<Col key={product._vendorCode} sm={12} md={6} lg={4} xl={3}>
						<Product product={product} />
					</Col>
				))}
			</Row>
		</>
	);
};

export const NewProducts = (): any => {
	const [products, setProducts] = useState<any[]>([]);

	useEffect((): any => {
		const fetchProducts = async () => {
			const { data } = await axios.get("/api/products");

			setProducts(data);
		};

		fetchProducts();
	}, []);

	const newProd = products.filter((product): any => product.new === true);

	return (
		<>
			<Row>
				{newProd.map((product): any => (
					<Col key={product._vendorÐ¡ode} sm={12} md={6} lg={4} xl={3}>
						<Product product={product} />
					</Col>
				))}
			</Row>
		</>
	);
};

export const CardProduct = ({
	match,
}: RouteComponentProps<CardProductProps>): any => {
	const [product, setProduct] = useState<any[]>([]);

	useEffect((): any => {
		const fetchProduct = async () => {
			const { data } = await axios.get(
				`/api/products/${match.params.vendorCode}`
			);

			setProduct(data);
		};

		fetchProduct();
	}, []);

	return (
		<>
			<Col md={2}>
				<Image src={product?.image} alt={product?.name} fluid></Image>
			</Col>
			<Col md={6}>
				<ListGroup variant='flush'>
					<ListGroup.Item>
						<Text variant='d3'>{product?.name}</Text>
					</ListGroup.Item>
					<ListGroup.Item>
						<Text variant='d4'>Price: {product?.price} $</Text>
					</ListGroup.Item>
				</ListGroup>
				<ListGroup variant='flush'>
					<ListGroup.Item>
						<Text variant='d5'> - Brand: {product?.brand}</Text>
						<Text variant='d5'> - Sex: {product?.sex}</Text>
						<Text variant='d5'> - Color: {product?.color}</Text>
						<Text variant='d5'> - Case: {product?.case}</Text>
						<Text variant='d5'>
							- Bracelet/strap: {product?.bracelet || product?.strap}
						</Text>
						<Text variant='d5'> - Material: {product?.material || "none"}</Text>
						<Text variant='d5'>
							{" "}
							- Movement: {product?.movement || "none"}{" "}
						</Text>
						<Text variant='d5'> - Dial: {product?.dial}</Text>
						<Text variant='d5'> - Case size: {product?.caseSize} mm</Text>
						<Text variant='d5'>
							- Water resistant to {product?.waterResistant} meters
						</Text>
					</ListGroup.Item>
				</ListGroup>
				<ListGroup variant='flush'>
					<ListGroup.Item>
						<Text variant='d5'>Count in stock: {product?.countInStock}</Text>
					</ListGroup.Item>
					<ListGroup.Item>
						<Button type='button' disabled={product?.countInStock === 0}>
							<Text variant='d5'>Add to Cart</Text>
						</Button>
						<StyledFavorites />
					</ListGroup.Item>
				</ListGroup>
			</Col>
		</>
	);
};
